import ElementsOfProgramming.Trees.TreeLeetcode;import Kunal_Kushwaha.LinkedList.Questions.LinkedListLeetcode;import javax.sound.midi.MidiFileFormat;import java.util.*;public class Trail implements LinkedListLeetcode, TreeLeetcode {    public static void main(String[] args) {        int[] sell = {9, 3, 5, 1, 4};        System.out.println(sell(sell, 0, sell.length - 1, 1));    }    static int sell(int[] arr, int i, int j, int year) {        if (i == arr.length || j < 0) return 0;        int first = 0, second = 0;        if (arr[i] < arr[j]) {            first = sell(arr, i + 1, j, year + 1);        } else {            second = sell(arr, i, j - 1, year + 1);        }        return Math.max(first, second) + year * arr[i];    }}