import java.util.Arrays;public class Trail {    public static void main(String[] args) {        int[][] arr = {                {1, 2, 3},                {4, 5, 6},                {6, 7, 8}        };        int i = 0, j;        while (i < arr.length) {            if (i % 2 == 0) {                j = 0;                while (j < arr[0].length) {                    System.out.print(arr[j][i] + " ");                    j++;                }            } else {                j = arr.length - 1;                while (j >= 0) {                    System.out.print(arr[j][i] + " ");                    j--;                }            }            i++;        }    }}