import ElementsOfProgramming.Trees.TreeLeetcode;import Kunal_Kushwaha.LinkedList.Questions.LinkedListLeetcode;import com.company.Array;import java.util.*;public class Trail implements LinkedListLeetcode, TreeLeetcode {    public static void main(String[] args) {        Trail s = new Trail();        Scanner sc = new Scanner(System.in);        String str = sc.nextLine(), text = sc.nextLine();        long[][] dp = new long[str.length()][text.length()];        for (long[] row : dp) {            Arrays.fill(row, -1);        }        System.out.println(util(str, 0, text, 0, dp));    }    public static long util(String s, int i, String t, int j, long[][] dp) {        if (j == t.length() || i == s.length()) return 0;        if (dp[i][j] != -1) return dp[i][j];        long inc = 0;        if (s.charAt(i) == t.charAt(j)) {            inc = 1 + util(s, i + 1, t, j + 1, dp);        } else {            long left = util(s, i + 1, t, j, dp);            long right = util(s, i, t, j + 1, dp);            inc = Math.max(left, right);        }        return dp[i][j] = inc;    }}